var Dispatcher = require('gifbin/flux.dispatcher.js');


module.exports = {
	//User
	login  : function(){
		Dispatcher.dispatch({
			type : 'LOGIN'
		});
	},
	logout : function(){
		Dispatcher.dispatch({
			type : 'LOGOUT'
		});
	},

	saveGif : function(gifData, callback){
		Dispatcher.dispatch({
			type : 'SAVE_GIF',
			payload : {
				data     : gifData,
				callback : callback
			}
		});
	},

	deleteGif : function(gifId, callback){
		Dispatcher.dispatch({
			type : 'DELETE_GIF',
			payload : {
				id : gifId,
				callback : callback
			}
		});
	},




	favGif : function(gifId){
		Dispatcher.dispatch({
			type : 'FAV_GIF',
			payload : gifId
		});
	},
	unfavGif : function(gifId){
		Dispatcher.dispatch({
			type : 'UNFAV_GIF',
			payload : gifId
		});
	},


}
