var Dispatcher = require('gifbin/flux.dispatcher.js');


module.exports = {
	//User
	login  : function(){
		Dispatcher.dispatch({
			type : 'LOGIN'
		});
	},
	logout : function(){
		Dispatcher.dispatch({
			type : 'LOGOUT'
		});
	},




	favGif : function(gifId){
		Dispatcher.dispatch({
			type : 'FAV_GIF',
			payload : gifId
		});
	},
	unfavGif : function(gifId){
		Dispatcher.dispatch({
			type : 'UNFAV_GIF',
			payload : gifId
		});
	},


}
